import{_ as a,c as i,o as r,ah as n}from"./chunks/framework.D8Wshugw.js";const f=JSON.parse('{"title":"Changelog","description":"","frontmatter":{},"headers":[],"relativePath":"changelog.md","filePath":"changelog.md"}'),s={name:"changelog.md"};function o(l,e,t,h,d,c){return r(),i("div",null,e[0]||(e[0]=[n('<h1 id="changelog" tabindex="-1">Changelog <a class="header-anchor" href="#changelog" aria-label="Permalink to “Changelog”">​</a></h1><h2 id="version-1-0-0" tabindex="-1">Version 1.0.0 <a class="header-anchor" href="#version-1-0-0" aria-label="Permalink to “Version 1.0.0”">​</a></h2><p>Initial Release</p><h2 id="version-1-1-0" tabindex="-1">Version 1.1.0 <a class="header-anchor" href="#version-1-1-0" aria-label="Permalink to “Version 1.1.0”">​</a></h2><h3 id="new" tabindex="-1">New <a class="header-anchor" href="#new" aria-label="Permalink to “New”">​</a></h3><ul><li><strong>NEW</strong> Clouds Night Sky Visiblity - Clouds are now moon-illuminated and visible at night.</li><li><strong>NEW</strong> Clouds Distortion - Improved cloud shapes through added distortion.</li><li><strong>NEW</strong> Atmosphere Blending - Effortlessly blend two skyboxes with minimal effort.</li><li><strong>NEW</strong> Continuous Sky - This new option prevents seams by rendering the ground as thes flipped sky, creating a seamless transition depending on your needs.</li></ul><h3 id="improvements" tabindex="-1">Improvements <a class="header-anchor" href="#improvements" aria-label="Permalink to “Improvements”">​</a></h3><ul><li>Significant Performance Improvements</li></ul><h3 id="fixes" tabindex="-1">Fixes <a class="header-anchor" href="#fixes" aria-label="Permalink to “Fixes”">​</a></h3><ul><li><strong>FIXED</strong> - Black cloud outline when using cloud detail on some platforms.</li><li><strong>FIXED</strong> - Low exposure in some demo skyboxes.</li></ul><h2 id="version-1-2-0" tabindex="-1">Version 1.2.0 <a class="header-anchor" href="#version-1-2-0" aria-label="Permalink to “Version 1.2.0”">​</a></h2><h3 id="new-1" tabindex="-1">New <a class="header-anchor" href="#new-1" aria-label="Permalink to “New”">​</a></h3><ul><li><strong>NEW</strong> Added animatable cubemap sky layers. You can now combine regular cubemap sky&#39;s with fast sky.</li><li><strong>NEW</strong> Added animatable cubemap stars.</li></ul><h3 id="improvements-1" tabindex="-1">Improvements <a class="header-anchor" href="#improvements-1" aria-label="Permalink to “Improvements”">​</a></h3><ul><li>Switched to half precision which incures significant performance improvements on mobile and other platforms.</li><li>Procedural star field can now be rotated in relation with time.</li><li>Refractored the shader code for better understanding. Useful for those willing to modify the shader.</li><li>Reduced branching in shaders, for better perrfomance</li></ul><h3 id="fixes-1" tabindex="-1">Fixes <a class="header-anchor" href="#fixes-1" aria-label="Permalink to “Fixes”">​</a></h3><ul><li>Issues on some graphics API&#39;s.</li></ul><h2 id="version-1-2-1" tabindex="-1">Version 1.2.1 <a class="header-anchor" href="#version-1-2-1" aria-label="Permalink to “Version 1.2.1”">​</a></h2><h3 id="fixes-2" tabindex="-1">Fixes <a class="header-anchor" href="#fixes-2" aria-label="Permalink to “Fixes”">​</a></h3><ul><li>Errors when the base asset folder is moved.</li></ul><h2 id="version-1-2-2" tabindex="-1">Version 1.2.2 <a class="header-anchor" href="#version-1-2-2" aria-label="Permalink to “Version 1.2.2”">​</a></h2><h3 id="fixes-3" tabindex="-1">Fixes <a class="header-anchor" href="#fixes-3" aria-label="Permalink to “Fixes”">​</a></h3><ul><li>Rendering issues on platforms with support for &#39;half&#39; precision, such as mobile platforms.</li></ul>',23)]))}const g=a(s,[["render",o]]);export{f as __pageData,g as default};
